    if (rpc_read(conn, &device, sizeof(nvmlDevice_t)) < 0 ||
        rpc_read(conn, &isEnabled, sizeof(unsigned int)) < 0 ||
        rpc_read(conn, &defaultMode, sizeof(unsigned int)) < 0)
        return -1;

    int request_id = rpc_end_request(conn);
