    int request_id = rpc_end_request(conn);
    if (request_id < 0)
        return -1;

    CUresult result = cuLibraryLoadFromFile(&library, &fileName, &jitOptions, &jitOptionsValues, numJitOptions, &libraryOptions, &libraryOptionValues, numLibraryOptions);

