        return -1;

    int request_id = rpc_end_request(conn);
    if (request_id < 0)
        return -1;

    CUresult result = cuMemAllocManaged(&dptr, bytesize, flags);

    if (rpc_start_response(conn, request_id) < 0)
        return -1;

    if (rpc_write(conn, &dptr, sizeof(CUdeviceptr)) < 0)
        return -1;

